</style>
<div id="app" v-loading.fullscreen.lock="loading" element-loading-spinner="el-icon-loading" element-loading-background="rgba(0, 0, 0, 0.8)">
    <div>
        <button type="button" class="btn btn-outline-primary btn-sm float-right" @click="openCrear()">Crear nuevo material</button>
        <h3 class='modal-title' id='materialLabel'>Material</h3>

        <div class="row">
            <div class="col-sm">
                <label for="clase">Clase</label>
                <el-select style="width: 100%;" size="small" @change="changeSubclase()" v-model="clasesSelected" filterable placeholder="Seleccione una clase">
                    <el-option value="">Seleccione una clase</el-option>
                    <el-option v-for="(d,k) in clasesList" :key="`c${d.id}`" :title="d.nombre" :label="`${d.id} - ${d.nombre}`" :value="d.id">
                        {{d.id}} - {{d.nombre}}
                    </el-option>
                </el-select>

            </div>
            <div class="col-sm">
                <label for="subclase">Subclase</label>
                <el-select style="width: 100%;" size="small" v-model="subclasesSelected" filterable placeholder="Seleccione una Sub clase">
                    <el-option value="">Seleccione una Sub clase</el-option>
                    <el-option v-for="(d,k) in subclasesList" :key="`sc${d.id}`" :title="d.nombre" :label="`${d.id} - ${d.nombre}`" :value="d.id">
                        {{d.id}} - {{d.nombre}}
                    </el-option>
                </el-select>

            </div>
        </div>
        <br>
        <button class="btn btn-success btn-sm btn-block my-2" @click="exportData(info,'Materiales')" v-if="info.length>0"><i class="fa fa-file-excel-o"></i> Excel</button>
        <br>

        <div class="table-responsive" v-if="info.length>0">
            <input type="text" class="form-control" placeholder="buscar por..." v-model="busqueda">
            <table class="table table-striped table-hover table-sm">
                <thead>
                    <tr>
                        <th>id</th>
                        <th>
                            <div style="width: 80px !important;"></div>sku
                        </th>
                        <th>sku_alterno</th>
                        <th>codigo_barras</th>
                        <th>descripcion</th>
                        <th>descripcion_corta</th>
                        <th>precio_base</th>
                        <th>Desayuno</th>
                        <th>Almuerzo</th>
                        <th>clase</th>
                        <th>subclase</th>
                        <th>activo</th>
                        <th>Stock</th>
                        <th></th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    <tr v-for="(d,k) in infoFiltered">
                        <td>{{d.id}}</td>
                        <td>{{d.sku}}</td>
                        <td>{{d.sku_alterno}}</td>
                        <td>{{d.codigo_barras}}</td>
                        <td>{{d.descripcion}}</td>
                        <td>{{d.descripcion_corta}}</td>
                        <td>{{numberFormat(d.precio_base*1.13)}}</td>
                        <td><i v-if="parseInt(d.desayuno)==1" class="el-icon-success text-success"></i> </i></td>
                        <td><i v-if="parseInt(d.almuerzo)==1" class="el-icon-success text-success"></i> </i></td>
                        <td>{{getClase(d)}}</td>
                        <td>{{getSubclase(d)}}</td>
                        <td><i v-if="parseInt(d.activo)==1" class="el-icon-success text-success"></i> <i v-else class="el-icon-error text-danger"> </td>
                        <td>{{d.stock}}</td>
                        <td><button class="btn btn-xs btn-warning" type="button" @click="editarMaterial(d)"><i class="fas fa-edit"></i> Editar</button></td>
                        <td><button v-if="parseInt(d.stock)==0" class="btn btn-xs btn-outline-danger" type="button" @click="eliminarMaterial(d)">eliminar</button></td>
                    </tr>
                </tbody>
            </table>
        </div>

        <div style="text-align: center;" v-else>
            <img width="100" src="data:image/png;base64," alt="">
            <h5>Sin información</h5>

        </div>
    </div>
    <div v-loading='loading' class='modal fade' id='materialModal' tabindex='-1' aria-labelledby='materialLabel' aria-hidden='true'>
        <div class='modal-dialog modal-xl '>
            <div class='modal-content'>
                <div class='modal-header'>

                    <button type='button' class='close' data-dismiss='modal' aria-label='Close'>
                        <span aria-hidden='true'>&times;</span>
                    </button>
                </div>
                <div class='modal-body' v-loading='loading'>

                    <div class="">
                        <h4><span v-if='estaCreando'>Crear</span><span v-else>Editar</span> Materiales </h4>
                        <div class="row">
                            <div class="col">
                                <div class="float-end">
                                    Activo?: <el-checkbox v-model="registro.activo">Activo</el-checkbox>
                                </div>
                            </div>
                            <div class="col">
                                <div class="float-end">
                                    Lleva Inventario?: <el-checkbox v-model="registro.lleva_inventario">Lleva Inventario</el-checkbox>
                                </div>
                            </div>
                            <div class="col" v-if="registro.stock>0">
                                <div class="float-end">
                                    <span style="font-size: 20px">
                                        Stock Actual: {{registro.stock}}
                                    </span>
                                </div>
                            </div>
                        </div>




                        <div class="row my-3 d-none">
                            <div class="col">
                                <label for="id">ID</label>
                                <input tooltip="id" v-model="registro.id" type="text" placeholder="id" formcontrolname="id" class="form-control form-control-sm" />
                            </div>
                        </div>


                        <div class="row my-3">
                            <div class="col-sm">
                                <label for="sku">SKU:</label>
                                <input tooltip="sku" readonly v-model="registro.sku" type="text" placeholder="SKU Generado Automatico" formcontrolname="sku" class="form-control form-control-sm" />
                            </div>
                            <div class="col-sm">
                                <label for="sku_alterno">SKU Alterno</label>
                                <input tooltip="sku_alterno" v-model="registro.sku_alterno" type="text" placeholder="Ingrese el SKU alterno" formcontrolname="sku_alterno" class="form-control form-control-sm" />
                            </div>
                            <div class="col-sm">
                                <label for="codigo_barras">Código de barras:</label>
                                <input tooltip="codigo_barras" v-model="registro.codigo_barras" type="text" placeholder="Ingrese el código_barras" formcontrolname="codigo_barras" class="form-control form-control-sm" />
                            </div>
                        </div>

                        <div class="row my-3">
                            <div class="col-sm">
                                <div class="row">
                                    <div class="col-sm">
                                        <label for="descripcion">Descripción:</label>
                                        <textarea class="form-control" name="descripcion" id="" v-model="registro.descripcion" cols="30" placeholder="Ingrese una descripción" rows="1"></textarea>
                                    </div>
                                </div>
                                <div class="row">
                                    <div class="col-sm">
                                        <label for="clase">Agregar composición:</label>
                                        <table>
                                            <tr>
                                                <td>Cantidad: <input type="number" v-model="cantidadMaterial" min="0" style="width:60px"></td>
                                                <td>
                                                    <el-select style="width: 100%;" size="small" v-model="materialSelected" filterable placeholder="Seleccione a agregar">
                                                        <el-option value="">Seleccione a agregar</el-option>
                                                        <el-option v-for="(d,k) in materiales" :key="`c${d.id}`" :title="`${d.sku} - ${d.descripcion}`" :label="`${d.sku} - ${d.descripcion}`" :value="d.id">
                                                        </el-option>
                                                    </el-select>
                                                </td>
                                                <td><button type="button" :class="`btn btn-sm btn-success ${(materialSelected!='')?'animate__animated animate__flash animate__slower animate__infinite':''}`" @click="addMaterial()">+</button></td>
                                            </tr>
                                        </table>

                                        <br>
                                        <table class="table" v-if="materialGroup.length>0">
                                            <tr>
                                                <td>SKU</td>
                                                <td>Desc.</td>
                                                <td>Cantidad</td>
                                                <td></td>
                                            </tr>
                                            <tr v-for="(d,k) in materialGroup">
                                                <td>{{d.hijo.sku}}</td>
                                                <td>{{d.hijo.descripcion}}</td>
                                                <td>{{d.cantidad}}</td>
                                                <td><button class="btn btn-xs btn-danger" @click="materialGroup.splice(k,1)">x</button></td>
                                            </tr>
                                        </table>

                                    </div>
                                </div>

                            </div>
                            <div class="col-sm">
                                <div class="row my-3">
                                    <div class="col">
                                        <label for="descripcion_corta">Descripción corta:</label>
                                        <input tooltip="descripcion_corta" v-model="registro.descripcion_corta" type="text" placeholder="Ingrese una descripción corta" formcontrolname="descripcion_corta" class="form-control form-control-sm" />
                                    </div>
                                </div>
                                <div class="row my-3">
                                    <div class="col">
                                        Activar como favoritos los dias: <br>
                                        <el-checkbox v-model="registro.lu">Lunes</el-checkbox>
                                        <el-checkbox v-model="registro.ma">Martes</el-checkbox>
                                        <el-checkbox v-model="registro.mi">Miércoles</el-checkbox>
                                        <el-checkbox v-model="registro.ju">Jueves</el-checkbox>
                                        <el-checkbox v-model="registro.vi">Viernes</el-checkbox>
                                        <el-checkbox v-model="registro.sa">Sábado</el-checkbox>
                                        <el-checkbox v-model="registro.do">Domingo</el-checkbox>
                                    </div>
                                </div>
                                <div class="row my-3">
                                    <div class="col">
                                        Activar en: <br>
                                        <el-checkbox v-model="registro.desayuno">Desayuno</el-checkbox>
                                        <el-checkbox v-model="registro.almuerzo">Almuerzo</el-checkbox>
                                        <el-checkbox v-model="registro.cena">Cena</el-checkbox>
                                    </div>
                                </div>
                            </div>
                        </div>

                        <div class="row my-3">
                            <div class="col-sm">
                                <label for="precio_iva">Precio con IVA:</label>
                                <input tooltip="precio_iva" @input="changeIva()" v-model="registro.precio_iva" type="number" placeholder="Ingrese el precio base" formcontrolname="precio_iva" class="form-control form-control-sm" />
                                <br>
                                <label for="precio_base">Precio base(Sin IVA):</label>
                                <input tooltip="precio_base" @input="changeBase()" v-model="registro.precio_base" type="number" placeholder="Ingrese el precio base" formcontrolname="precio_base" class="form-control form-control-sm" />
                            </div>
                            <div class="col-sm">
                                <label for="peso_bruto">Peso bruto:</label>
                                <input tooltip="peso_bruto" v-model="registro.peso_bruto" type="number" placeholder="Ingrese el peso bruto" formcontrolname="peso_bruto" class="form-control form-control-sm" />
                            </div>
                            <div class="col-sm">
                                <label for="peso_neto">Peso neto:</label>
                                <input tooltip="peso_neto" v-model="registro.peso_neto" type="number" placeholder="Ingrese el peso neto" formcontrolname="peso_neto" class="form-control form-control-sm" />
                            </div>
                            <div class="col-sm">
                                <label for="costo_prom">Costo Promedio:</label>
                                <input tooltip="costo_prom" v-model="registro.costo_prom" type="number" placeholder="Ingrese el Costo promedio" formcontrolname="costo_prom" class="form-control form-control-sm" />
                            </div>
                        </div>

                        <div class="row my-3">
                            <div class="col-sm">
                                <label for="clase">Clase</label>
                                <el-select style="width: 100%;" size="small" @change="setSubclase()" v-model="registro.clase" filterable placeholder="Seleccione una clase">
                                    <el-option value="">Seleccione una clase</el-option>
                                    <el-option v-for="(d,k) in clasesList" :key="`c${d.id}`" :title="d.nombre" :label="d.nombre" :value="d.id">
                                    </el-option>
                                </el-select>
                            </div>
                            <div class="col-sm">
                                <label for="subclase">subclase</label>
                                <el-select style="width: 100%;" size="small" v-model="registro.subclase" filterable placeholder="Seleccione una Sub clase">
                                    <el-option value="">Seleccione una Sub clase</el-option>
                                    <el-option v-for="(d,k) in subclasesList" :key="`sc${d.id}`" :title="d.nombre" :label="d.nombre" :value="d.id">
                                    </el-option>
                                </el-select>

                            </div>
                        </div>

                        <div class="row my-3">
                            <div class="col">
                                <button v-if='estaCreando' type="button" class="btn btn-success btn-block" @click="createMaterial()">Guardar</button>
                                <button v-else type="button" class="btn btn-warning btn-block" @click="editMaterial()">Guardar</button>
                            </div>
                        </div>



                    </div>

                </div>
                <div class='modal-footer'>

                </div>
            </div>
        </div>
    </div>

</div>

<script>
    let app = new Vue({
        el: '#app',
        data: {
            busqueda: "",
            fechas: [dayjs().subtract(180, "days"), dayjs()],
            loading: true,
            isUpdating: false,
            estaCreando: true,
            clasesSelected: '',
            subclasesSelected: '',
            materialSelected: '',
            cantidadMaterial: 0,
            materialGroup: [],
            info: [],
            clasesList: <?= $clases ?>,
            subclasesList: [],
            subclasesListComplete: <?= $subclases ?>,
            registro: {
                id: "",
                sku: "",
                sku_alterno: "",
                codigo_barras: "",
                descripcion: "",
                descripcion_corta: "",
                precio_base: 0,
                precio_iva: 0,
                peso_bruto: "",
                peso_neto: "",
                clase: "",
                subclase: "",
                unidad_med_compra: "",
                cant_min: "",
                grupo_impuestos: "",
                udf1: "",
                udf2: "",
                udf3: "",
                udf4: "",
                umedida: "",
                umin: "",
                stock: 0,
                lu: false,
                ma: false,
                mi: false,
                ju: false,
                vi: false,
                sa: false,
                do: false,
                desayuno: false,
                almuerzo: false,
                cena: false,
                activo: true,
                lleva_inventario: true,
                costo_prom: 0,
            },
        },
        methods: {
            addMaterial() {
                if (!this.materialGroup.find(r => r.id == this.materialSelected)) {
                    let tmp = this.jclear(this.materiales.find(r => r.id == this.materialSelected));
                    this.materialGroup.push({
                        cantidad: parseFloat(this.cantidadMaterial),
                        material_id: tmp.id,
                        material_padre: 0,
                        hijo: {
                            sku: tmp.sku,
                            descripcion: tmp.descripcion,
                            id: tmp.id
                        },
                    });
                }
                this.materialSelected = ''
                this.cantidadMaterial = 0

            },
            changeSubclase() {
                this.subclasesSelected = ''
                if (this.clasesSelected == "") {
                    this.subclasesList = [];
                } else {
                    this.subclasesList = [];
                    this.subclasesList = JSON.parse(JSON.stringify(this.subclasesListComplete.filter(r => r.clase_id == this.clasesSelected)));
                }
            },
            eliminarMaterial(d) {
                this.$confirm('Desea mostrar eliminar el registro?', 'Warning', {
                    confirmButtonText: 'OK',
                    cancelButtonText: 'Cancelar',
                    type: 'warning'
                }).then(() => {
                    this.loading = true;
                    axios.post('<?= route('deleteMaterial') ?>', {
                            datos: {
                                id: d.id,
                            },
                        })
                        .then(res => {
                            app.loading = false;
                            app.si('Eliminado con exito');
                            app.getData();
                        })
                        .catch(err => {
                            console.error(err);
                            app.loading = false;
                        })
                }).catch(() => {});
            },
            editarMaterial(d) {
                this.estaCreando = false
                this.limpiarRegistro();
                this.registro = this.jclear(d)

                this.registro.lu = (parseInt(this.registro.lu) == 1) ? true : false
                this.registro.ma = (parseInt(this.registro.ma) == 1) ? true : false
                this.registro.mi = (parseInt(this.registro.mi) == 1) ? true : false
                this.registro.ju = (parseInt(this.registro.ju) == 1) ? true : false
                this.registro.vi = (parseInt(this.registro.vi) == 1) ? true : false
                this.registro.sa = (parseInt(this.registro.sa) == 1) ? true : false
                this.registro.do = (parseInt(this.registro.do) == 1) ? true : false
                this.registro.desayuno = (parseInt(this.registro.desayuno) == 1) ? true : false
                this.registro.almuerzo = (parseInt(this.registro.almuerzo) == 1) ? true : false
                this.registro.cena = (parseInt(this.registro.cena) == 1) ? true : false
                this.registro.activo = (parseInt(this.registro.activo) == 1) ? true : false
                this.registro.lleva_inventario = (parseInt(this.registro.lleva_inventario) == 1) ? true : false
                this.registro.clase = parseInt(this.registro.clase)
                //this.setSubclase()
                this.registro.subclase = parseInt(this.registro.subclase)
                this.materialGroup = this.jclear(this.registro.detalles)
                this.cantidadMaterial = 0
                //rellenar subclase
                this.subclasesList = [];
                let clase = JSON.parse(JSON.stringify(this.clasesList.find(r => r.id == this.registro.clase)))
                this.subclasesList = clase.subclases;

                this.changeBase()

                $('#materialModal').modal();
            },
            openCrear() {
                this.estaCreando = true
                this.limpiarRegistro();
                $('#materialModal').modal();
                this.materialGroup = []
                this.cantidadMaterial = 0
            },
            validacion() {
                if (this.registro.descripcion == '' || this.registro.descripcion == null) {
                    this.no('Ingrese una descripción');
                    return;
                }
                /*if (this.registro.descripcion_corta=='' || this.registro.descripcion_corta==null) {
                   this.no('Ingrese una descripción corta');
                   return;
                }*/

                if (this.registro.clase == '' || this.registro.clase == null) {
                    this.no('debe seleccionar una clase');
                    return;
                }
                if (this.registro.subclase == '' || this.registro.subclase == null) {
                    this.no('debe seleccionar una subclase');
                    return;
                }
                return true;
            },
            getClase(v) {
                let res = this.clasesList.find(r => r.id == v.clase)
                return (res) ? res.nombre : '-';
            },
            getSubclase(v) {
                let res = this.subclasesListComplete.find(r => r.id == v.subclase)
                return (res) ? res.nombre : '-';
            },
            setSubclase() {
                this.registro.subclase = ''
                if (this.registro.clase == "") {
                    this.subclasesList = [];
                } else {
                    this.subclasesList = [];
                    let clase = JSON.parse(JSON.stringify(this.clasesList.find(r => r.id == this.registro.clase)))
                    this.subclasesList = clase.subclases;
                }
            },
            limpiarRegistro() {
                this.registro.id = "";
                this.registro.sku = "";
                this.registro.sku_alterno = "";
                this.registro.codigo_barras = "";
                this.registro.descripcion = "";
                this.registro.descripcion_corta = "";
                this.registro.precio_base = 0;
                this.registro.precio_iva = 0;
                this.registro.peso_bruto = "";
                this.registro.peso_neto = "";
                this.registro.clase = "";
                this.registro.subclase = "";
                this.registro.unidad_med_compra = "";
                this.registro.cant_min = "";
                this.registro.grupo_impuestos = "";
                this.registro.udf1 = "";
                this.registro.udf2 = "";
                this.registro.udf3 = "";
                this.registro.udf4 = "";
                this.registro.umedida = "";
                this.registro.umin = "";
                this.registro.lu = false;
                this.registro.ma = false;
                this.registro.mi = false;
                this.registro.ju = false;
                this.registro.vi = false;
                this.registro.sa = false;
                this.registro.do = false;
                this.registro.desayuno = false;
                this.registro.almuerzo = false;
                this.registro.cena = false;
                this.registro.activo = true;
                this.registro.lleva_inventario = true;
                this.registro.stock = 0;
                this.registro.costo_prom = 0;
            },
            getData() {
                this.loading = true;
                axios.post('<?= route('getMateriales') ?>', {})
                    .then(res => {
                        app.info = res.data;
                        this.loading = false;
                        //console.log(res)
                    })
                    .catch(err => {
                        this.loading = false;
                        //console.error(err);
                    });
            },
            createMaterial() {
                if (!this.validacion()) {
                    return
                };

                this.loading = true;
                axios.post('<?= route('material.store') ?>', {
                        datos: app.registro,
                        extra: app.materialGroup,
                    })
                    .then(res => {
                        app.loading = false;
                        $('#materialModal').modal('hide');
                        app.si('Creado con exito');
                        app.getData();

                    })
                    .catch(err => {
                        console.error(err);
                        app.no(err.response.data.msg)
                        app.loading = false;
                    });
            },
            editMaterial() {
                if (!this.validacion()) {
                    return
                };
                this.loading = true;
                axios.post('<?= route('material.edit') ?>', {
                        datos: app.registro,
                        extra: app.materialGroup,
                    })
                    .then(res => {
                        app.loading = false;
                        $('#materialModal').modal('hide');
                        app.si('Editado con exito');
                        app.getData();

                    })
                    .catch(err => {
                        console.error(err);
                        app.loading = false;
                    });
            },
            changeIva() {
                if (!this.registro.isUpdating) {
                    this.registro.isUpdating = true;
                    if (this.registro.precio_iva !== '' && parseFloat(this.registro.precio_iva) !== 0) {
                        this.registro.precio_base = (parseFloat(this.registro.precio_iva) / 1.13);
                    } else {
                        this.registro.precio_base = 0;
                    }
                    this.registro.isUpdating = false;
                }
            },
            changeBase() {
                if (!this.registro.isUpdating) {
                    this.registro.isUpdating = true;
                    if (this.registro.precio_base !== '' && parseFloat(this.registro.precio_base) !== 0) {
                        this.registro.precio_iva = parseFloat((parseFloat(this.registro.precio_base) * 1.13).toFixed(2));
                    } else {
                        this.registro.precio_iva = 0;
                    }
                    this.registro.isUpdating = false;
                }
            },
            dateFormater(d, f = 'DD/MM/YYYY HH:mm') {
                return (dayjs(d).format(f) == 'Invalid Date') ? '-' : dayjs(d).format(f)
            },
            timeFormater(d) {
                return dayjs(d).format('HH:mm')
            },
            numberFormat(n) {
                const numberWithTwoDecimals = Number(n).toFixed(2);
                let v = numberWithTwoDecimals.toLocaleString('es-MX', {
                    minimumFractionDigits: 2, // Asegura que siempre haya 2 decimales
                    maximumFractionDigits: 2, // Asegura que siempre haya 2 decimales
                });

                const numberAsString = v.toString();

                // Divide la cadena en parte entera y parte decimal (si la tiene)
                const parts = numberAsString.split('.');
                const integerPart = parts[0];
                const decimalPart = parts.length > 1 ? '.' + parts[1] : '';

                // Aplica la expresión regular para insertar comas como separadores de miles en la parte entera
                const integerWithCommas = integerPart.replace(/\B(?=(\d{3})+(?!\d))/g, ',');

                // Combina la parte entera y la parte decimal nuevamente
                const formattedNumber = integerWithCommas + decimalPart;

                return formattedNumber;
            },
            si(mensaje) {
                app.$message({
                    message: mensaje,
                    type: 'success'
                });
            },
            no(mensaje) {
                app.$message({
                    message: mensaje,
                    type: 'warning'
                });
            },
            jclear(d) {
                return JSON.parse(JSON.stringify(d));
            },
            exportData(json, nombre) {
                filename = nombre + dayjs().format('DDMMYYYYHHmm') + '.xlsx';
                data = json
                var ws = XLSX.utils.json_to_sheet(data);
                var wb = XLSX.utils.book_new();
                XLSX.utils.book_append_sheet(wb, ws, "reporte");
                XLSX.writeFile(wb, filename);
            }
        },
        computed: {
            materiales() {
                return this.info.map(r => {
                    return {
                        "id": r.id,
                        "sku": r.sku,
                        "descripcion": r.descripcion,
                        "descripcion_corta": r.descripcion_corta
                    }
                });
            },
            infoFiltered() {
                let data = [];
                data = this.jclear(this.info)
                if (this.info.length > 0) {

                    if (this.clasesSelected != '') {
                        data = data.filter((i) => {
                            return i.clase == this.clasesSelected
                        });
                    }
                    if (this.subclasesSelected != '') {
                        data = data.filter((i) => {
                            return i.subclase == this.subclasesSelected
                        });
                    }


                    if (this.busqueda != '') {
                        data = data.filter(b => (
                            (b.descripcion?.includes(this.busqueda.toUpperCase())) ||
                            (b.descripcion_corta?.includes(this.busqueda.toUpperCase())) ||
                            (b.sku?.includes(this.busqueda.toUpperCase())) ||
                            (b.sku_alterno?.toUpperCase().includes(this.busqueda.toUpperCase())) ||
                            (b.codigo_barras?.toUpperCase().includes(this.busqueda.toUpperCase())) ||
                            ((this.getClase({
                                clase: parseInt(b.clase)
                            }))?.toUpperCase().includes(this.busqueda?.toUpperCase())) ||
                            ((this.getSubclase({
                                subclase: parseInt(b.subclase)
                            }))?.toUpperCase().includes(this.busqueda?.toUpperCase()))
                        ))
                    }
                }
                return data
            }

        },
        mounted() {
            this.loading = false;
            this.getData()
        },
        watch: {
            
        }
    });
</script>

@endverbatim
